const slider=document.querySelector(".slider"),prevButton=document.querySelector(".prev"),nextButton=document.querySelector(".next"),sliderImages=document.querySelectorAll(".slider img");let currentSlide=0;function showSlide(index){sliderImages.forEach(slide=>slide.classList.remove("active")),sliderImages[index].classList.add("active")}function scrollToSlide(index){const slide=sliderImages[index],slideLeft=slide.offsetLeft,slideWidth=slide.offsetWidth,sliderLeft=slider.offsetLeft,sliderWidth=slider.offsetWidth;slider.scrollLeft=slideLeft-sliderLeft-sliderWidth/2+slideWidth/2}function nextSlide(){currentSlide=(currentSlide+1)%sliderImages.length,showSlide(currentSlide),scrollToSlide(currentSlide)}function prevSlide(){currentSlide=(currentSlide-1+sliderImages.length)%sliderImages.length,showSlide(currentSlide),scrollToSlide(currentSlide)}prevButton.addEventListener("click",prevSlide),nextButton.addEventListener("click",nextSlide);let interval=setInterval(nextSlide,5e3);slider.addEventListener("mouseenter",()=>clearInterval(interval)),slider.addEventListener("mouseleave",()=>interval=setInterval(nextSlide,5e3));